sys_ws_operation.operation_script:
(function() {
    try {
        gs.debug("Starting script execution");

        var instanceName = 'dev123716';  // Replace with your ServiceNow instance name
        var user = 'admin';  // Replace with your ServiceNow username
        var password = 'KItt48++';  // Replace with your ServiceNow password

        var request = new sn_ws.RESTMessageV2();
        request.setEndpoint('https://' + instanceName + '.service-now.com/api/x_938832_octomobil/komp_buildings_data/stn_all_onus');
        request.setHttpMethod('GET');

        // Set basic authentication with your ServiceNow credentials
        request.setBasicAuth(user, password);
        request.setRequestHeader("Accept", "application/json");

        var response = request.execute();
        var jsonResponse = JSON.parse(response.getBody());

        // Loop through the JSON response and insert data into your custom table
        for (var buildingName in jsonResponse) {
            if (jsonResponse.hasOwnProperty(buildingName)) {
                var onus = jsonResponse[buildingName];

                for (var i = 0; i < onus.length; i++) {
                    var serialCode = onus[i].Serial_Code;

                    // Create a new record in your custom table for each serial code
                    var gr = new GlideRecord('x_938832_octomobil_stn_kompdata');
                    gr.initialize();
                    gr.setValue('building_name', buildingName);
                    gr.setValue('serial_code', serialCode);
                    gr.insert();
                }
            }
        }

        gs.debug("Script execution complete");
    } catch (ex) {
        gs.debug("Exception: " + ex.toString());
    }
})();
