sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        // Define the URL of the company's API
        var apiUrl = 'http://app.sasakonnect.net:13000/api/onus/stn';

        // Set the headers for the API request
        var headers = {
            'Authorization': 'Bearer SX10u7hcvNVDUAGkIkV0SoCspfJGk6DXdFqNmwLHS2zsOGA1ruJ4t3fPMgZsT2mCeW5nMSeSK06KGPMH',
        };

        // Make an HTTP GET request to the company's API using ServiceNow RESTMessageV2
        var requestMessage = new sn_ws.RESTMessageV2();
        requestMessage.setHttpMethod('get');
        requestMessage.setEndpoint(apiUrl);
        requestMessage.setRequestHeader('Authorization', headers['Authorization']);

        // Execute the request
        var responseFromCompanyAPI = requestMessage.execute();

        // Check the HTTP response status
        var statusCode = responseFromCompanyAPI.getStatusCode();

        if (statusCode === 200) {
            // Get the JSON response body
            var responseBody = responseFromCompanyAPI.getBody();
            var parsedData = JSON.parse(responseBody);

            // Group the data by BuildingName
            var groupedData = {};

            for (var i = 0; i < parsedData.length; i++) {
                var entry = parsedData[i];
                var buildingName = entry.BuildingName;

                if (!groupedData[buildingName]) {
                    groupedData[buildingName] = [];
                }

                // Only add the relevant fields to the group
                groupedData[buildingName].push({
                    //'BuildingName': buildingName,
                    'Serial_Code': entry.Serial_Code,
                    //'Region': entry.Region
                });
            }

            // Set the successful response
            response.setStatus(200);
            response.setBody(groupedData);
        } else {
            // Handle error cases here
            response.setStatus(statusCode);
            response.setBody('Error fetching data from the company\'s API. Status code: ' + statusCode);
        }
    } catch (ex) {
        // Handle exceptions
        response.setStatus(500);
        response.setBody('Exception: ' + ex.toString());
    }
})(request, response);
