sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        // Fetch data from the company's API
        var companyAPIUrl = 'https://dev123716.service-now.com/api/x_938832_octomobil/komp_buildings_data/stn_all_onus';

        var companyResponse = new sn_ws.RESTMessageV2();
        companyResponse.setHttpMethod('get');
        companyResponse.setEndpoint(companyAPIUrl);

        // You may need to set headers or authentication for the company API if required.

        var companyResult = companyResponse.execute();
        var companyData = JSON.parse(request.response.body);

        // Fetch data from the Elasticsearch API
        var elasticAPIUrl = 'https://dev123716.service-now.com/api/x_938832_octomobil/elasticsearch_data/api/stn-elastdata-api';

        var elasticResponse = new sn_ws.RESTMessageV2();
        elasticResponse.setHttpMethod('get');
        elasticResponse.setEndpoint(elasticAPIUrl);

        // You may need to set headers or authentication for the Elasticsearch API if required.

        var elasticResult = elasticResponse.execute();
        var elasticData = JSON.parse(elasticResult.getBody());

        // Process data and determine building status
        var buildingStatus = {};

        // Loop through data from the company's API
        for (var building in companyData) {
            if (companyData.hasOwnProperty(building)) {
                var onus = companyData[building];

                // Check the corresponding data from the Elasticsearch API for each ONU
                var isBuildingOnline = true; // Assume the building is online

                for (var i = 0; i < onus.length; i++) {
                    var onu = onus[i];
                    var onuSerialCode = onu.Serial_Code;

                    // Find the corresponding data in Elasticsearch
                    if (elasticData[building]) {
                        for (var j = 0; j < elasticData[building].length; j++) {
                            var elasticOnu = elasticData[building][j];
                            if (elasticOnu.serialNumber === onuSerialCode) {
                                // Check if the ONU is online (ifOperStatus is 1)
                                if (elasticOnu.ifOperStatus !== 1) {
                                    isBuildingOnline = false;
                                    break;
                                }
                            }
                        }
                    }
                }

                // Store the building status
                buildingStatus[building] = isBuildingOnline;
            }
        }

        // Set the response based on the building status, and stringify the JSON
        response.setStatus(200);
        response.setBody(JSON.stringify(buildingStatus));
    } catch (ex) {
        response.setStatus(500);
        response.setBody('Exception: ' + ex.toString());
    }
})(request, response);
